def inputfileNameNumering(num):
    return "_DataForModularCurves2/lmfdbDataRanks" + str(num) + ".csv"

def outputfileNameNumering(num):
    return "_DataForModularCurves3/NamedlmfdbDataRanks" + str(num) + ".csv"


def merge_files(file_paths, output_file):
    """Merges the content of multiple files into a single output file.

    Args:
        file_paths: A list of file paths to be merged.
        output_file: The path to the output file.
    """
    with open(output_file, 'w') as outfile:
        for file_path in file_paths:
            try:
                with open(file_path, 'r') as infile:
                    outfile.write(infile.read())
            except FileNotFoundError:
                print(f"File not found: {file_path}")

